import React from "react";
import PropTypes from "prop-types";
import TableDropdown from "components/Dropdowns/TableDropdown.js";

export default function CardPersonnels({ color = "light" }) {
  const personnels = [
    { id: 1, nom: "Jean Dupont", dateNaissance: "1990-05-12", departement: "Informatique" },
    { id: 2, nom: "Sophie Martin", dateNaissance: "1985-09-23", departement: "Ressources Humaines" },
    { id: 3, nom: "Ali Ben Salah", dateNaissance: "1992-11-02", departement: "Finance" },
  ];

  return (
    <div
      className={
        "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
        (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
      }
    >
      <div className="rounded-t mb-0 px-4 py-3 border-0">
        <div className="flex flex-wrap items-center justify-between">
          <h3
            className={
              "font-semibold text-lg " +
              (color === "light" ? "text-blueGray-700" : "text-white")
            }
          >
            Liste des personnels
          </h3>
          <button className="bg-blue-500 text-white px-4 py-2 rounded">Ajouter Personnel</button>
        </div>
      </div>
      <div className="block w-full overflow-x-auto">
        <table className="items-center min-w-full bg-transparent border-collapse">
          <thead>
            <tr>
              {["Id", "Nom et prénom", "Date de naissance", "Départements", "Action"].map((header, index) => (
                <th
                  key={index}
                  className={
                    "px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left " +
                    (color === "light"
                      ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                      : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                  }
                >
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {personnels.map((personnel) => (
              <tr key={personnel.id} className="hover:bg-gray-100">
                <td className="px-6 py-4 border-b border-gray-200 text-sm">{personnel.id}</td>
                <td className="px-6 py-4 border-b border-gray-200 text-sm">{personnel.nom}</td>
                <td className="px-6 py-4 border-b border-gray-200 text-sm">{personnel.dateNaissance}</td>
                <td className="px-6 py-4 border-b border-gray-200 text-sm">{personnel.departement}</td>
                <td className="px-6 py-4 border-b border-gray-200 text-sm">
                  <button className="bg-blue-500 text-white px-3 py-1 rounded mr-2">Voir</button>
                  <button className="bg-yellow-500 text-white px-3 py-1 rounded mr-2">Modifier</button>
                  <button className="bg-red-500 text-white px-3 py-1 rounded">Supprimer</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

CardPersonnels.propTypes = {
  color: PropTypes.string,
};
