import React, { useState } from "react";
import PropTypes from "prop-types";
import { MdDelete, MdVisibility, MdEdit } from "react-icons/md";

export default function CardPersonnels({ color = "light" }) {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOption, setSortOption] = useState("");
  const [personnels, setPersonnels] = useState([
    { id: 1, nom: "Jean Dupont", departement: "Informatique" },
    { id: 2, nom: "Sophie Martin", departement: "Ressources Humaines" },
    { id: 3, nom: "Ali Ben Salah", departement: "Finance" },
  ]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newPersonnel, setNewPersonnel] = useState({ nom: "", departement: "" });

  const filteredPersonnels = personnels
    .filter((personnel) =>
      isNaN(searchTerm)
        ? personnel.nom.toLowerCase().includes(searchTerm.toLowerCase())
        : personnel.id.toString().includes(searchTerm)
    )
    .sort((a, b) => {
      if (!sortOption) return 0;
      if (sortOption === "id-asc") return a.id - b.id;
      if (sortOption === "id-desc") return b.id - a.id;
      if (sortOption === "departement") return a.departement.localeCompare(b.departement);
      return 0;
    });

  const handleAction = (action, id) => {
    if (action === "supprimer") {
      setPersonnels(personnels.filter((personnel) => personnel.id !== id));
    } else {
      alert(`${action} le personnel avec l'ID ${id}`);
    }
  };

  const handleAddPersonnel = () => {
    setPersonnels([...personnels, { id: personnels.length + 1, ...newPersonnel }]);
    setIsModalOpen(false);
    setNewPersonnel({ nom: "", departement: "" });
  };

  return (
    <div className={`relative flex flex-col w-full shadow-lg rounded ${color === "light" ? "bg-white" : "bg-lightBlue-900 text-white"}`}>
      <div className="rounded-t px-4 py-3 border-0 flex justify-between items-center">
        <h3 className="font-semibold text-lg text-blueGray-700">Liste des personnels</h3>
        <button onClick={() => setIsModalOpen(true)} className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600">
          Ajouter un personnel
        </button>
      </div>
      <div className="block w-full overflow-x-auto">
        <table className="w-full bg-transparent border-collapse">
          <thead>
            <tr>
              {["Id", "Nom", "DÃ©partement", "Action"].map((header, index) => (
                <th key={index} className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50 text-blueGray-500">
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filteredPersonnels.map((personnel) => (
              <tr key={personnel.id}>
                <td className="px-6 py-4 border-b text-sm">{personnel.id}</td>
                <td className="px-6 py-4 border-b text-sm">{personnel.nom}</td>
                <td className="px-6 py-4 border-b text-sm">{personnel.departement}</td>
                <td className="px-6 py-4 border-b text-sm flex gap-2">
                  <button onClick={() => handleAction("voir", personnel.id)} className="bg-green-500 text-white p-2 rounded">
                    <MdVisibility />
                  </button>
                  <button onClick={() => handleAction("modifier", personnel.id)} className="bg-yellow-500 text-white p-2 rounded">
                    <MdEdit />
                  </button>
                  <button onClick={() => handleAction("supprimer", personnel.id)} className="bg-red-500 text-white p-2 rounded">
                    <MdDelete />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h2 className="text-xl font-semibold mb-4">Ajouter un personnel</h2>
            <input
              type="text"
              placeholder="Nom"
              value={newPersonnel.nom}
              onChange={(e) => setNewPersonnel({ ...newPersonnel, nom: e.target.value })}
              className="w-full px-3 py-2 border rounded mb-3"
            />
            <input
              type="text"
              placeholder="DÃ©partement"
              value={newPersonnel.departement}
              onChange={(e) => setNewPersonnel({ ...newPersonnel, departement: e.target.value })}
              className="w-full px-3 py-2 border rounded mb-3"
            />
            <div className="flex justify-end gap-2">
              <button onClick={() => setIsModalOpen(false)} className="px-4 py-2 bg-gray-400 text-white rounded-lg">Annuler</button>
              <button onClick={handleAddPersonnel} className="px-4 py-2 bg-blue-500 text-white rounded-lg">Ajouter</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

CardPersonnels.propTypes = {
  color: PropTypes.string,
};
