        /* Styles généraux */
        .container {
          background-color: #f5f7fa;
          min-height: 100vh;
          padding: 20px;
          font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }

        .card {
          max-width: 1200px;
          margin: 0 auto;
          background-color: white;
          border-radius: 8px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
          overflow: hidden;
        }

        /* Header */
        .header {
          padding: 20px;
          border-bottom: 1px solid #e2e8f0;
        }

        .header h1 {
          font-size: 24px;
          font-weight: bold;
          color: #1e293b;
          margin: 0 0 8px 0;
        }

        .header p {
          color: #64748b;
          margin: 0;
        }

        /* Toolbar */
        .toolbar {
          padding: 16px;
          border-bottom: 1px solid #e2e8f0;
          display: flex;
          justify-content: space-between;
          align-items: center;
          flex-wrap: wrap;
          gap: 16px;
        }

        .toolbar-left {
          display: flex;
          flex-wrap: wrap;
          gap: 16px;
          flex: 1;
        }

        .search-container {
          position: relative;
          min-width: 250px;
        }

        .search-input {
          width: 100%;
          padding: 8px 16px 8px 40px;
          border: 1px solid #cbd5e1;
          border-radius: 6px;
          font-size: 14px;
          transition: border-color 0.2s, box-shadow 0.2s;
        }

        .search-input:focus {
          outline: none;
          border-color: #3b82f6;
          box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.3);
        }

        .search-icon {
          position: absolute;
          left: 12px;
          top: 50%;
          transform: translateY(-50%);
          color: #94a3b8;
        }

        .tabs {
          display: flex;
          gap: 8px;
        }

        .tab {
          padding: 8px 16px;
          border-radius: 6px;
          background-color: #f1f5f9;
          border: none;
          color: #64748b;
          font-weight: 500;
          cursor: pointer;
          transition: background-color 0.2s, color 0.2s;
        }

        .tab:hover {
          background-color: #e2e8f0;
        }

        .tab.active {
          background-color: #3b82f6;
          color: white;
        }

        .btn-primary {
          background-color: #3b82f6;
          color: white;
          border: none;
          padding: 8px 16px;
          border-radius: 6px;
          font-weight: 500;
          cursor: pointer;
          display: flex;
          align-items: center;
          gap: 8px;
          transition: background-color 0.2s;
        }

        .btn-primary:hover {
          background-color: #2563eb;
        }

        .btn-primary .icon {
          font-size: 18px;
        }

        /* Table */
        .table-container {
          overflow-x: auto;
        }

        table {
          width: 100%;
          border-collapse: collapse;
        }

        th {
          text-align: left;
          padding: 12px 16px;
          background-color: #f8fafc;
          color: #64748b;
          font-size: 12px;
          font-weight: 600;
          text-transform: uppercase;
          letter-spacing: 0.05em;
          border-bottom: 1px solid #e2e8f0;
        }

        th.sortable {
          cursor: pointer;
        }

        .th-content {
          display: flex;
          align-items: center;
          gap: 4px;
        }

        .sort-icon {
          display: inline-block;
          font-size: 10px;
        }

        td {
          padding: 16px;
          border-bottom: 1px solid #e2e8f0;
          color: #334155;
        }

        tr:hover {
          background-color: #f8fafc;
        }

        .formation-title {
          display: flex;
          flex-direction: column;
        }

        .formation-title .title {
          font-weight: 500;
          color: #1e293b;
        }

        .formation-title .description {
          font-size: 14px;
          color: #64748b;
          margin-top: 4px;
        }

        .with-icon {
          display: flex;
          align-items: center;
          gap: 8px;
        }

        .with-icon .icon {
          color: #94a3b8;
          font-size: 16px;
        }

        .dot {
          display: inline-block;
          width: 8px;
          height: 8px;
          border-radius: 50%;
          margin-left: 8px;
        }

        .yellow-dot {
          background-color: #facc15;
        }

        .status {
          display: inline-block;
          padding: 4px 8px;
          border-radius: 9999px;
          font-size: 12px;
          font-weight: 600;
        }

        .status-blue {
          background-color: #dbeafe;
          color: #1e40af;
        }

        .status-yellow {
          background-color: #fef9c3;
          color: #854d0e;
        }

        .status-green {
          background-color: #dcfce7;
          color: #166534;
        }

        .status-gray {
          background-color: #f1f5f9;
          color: #475569;
        }

        .action-buttons {
          display: flex;
          gap: 8px;
        }

        .btn-edit, .btn-delete {
          background: none;
          border: none;
          cursor: pointer;
          font-size: 16px;
          padding: 4px;
          border-radius: 4px;
          transition: background-color 0.2s;
        }

        .btn-edit {
          color: #3b82f6;
        }

        .btn-edit:hover {
          background-color: #dbeafe;
        }

        .btn-delete {
          color: #ef4444;
        }

        .btn-delete:hover {
          background-color: #fee2e2;
        }

        .btn-confirm, .btn-cancel {
          background: none;
          border: none;
          cursor: pointer;
          font-size: 14px;
          padding: 4px 8px;
          border-radius: 4px;
          display: flex;
          align-items: center;
          gap: 4px;
          transition: background-color 0.2s;
        }

        .btn-confirm {
          color: #ef4444;
        }

        .btn-confirm:hover {
          background-color: #fee2e2;
        }

        .btn-cancel {
          color: #64748b;
        }

        .btn-cancel:hover {
          background-color: #f1f5f9;
        }

        .empty-table {
          text-align: center;
          color: #94a3b8;
          padding: 32px;
        }

        /* Pagination */
        .pagination {
          padding: 16px;
          border-top: 1px solid #e2e8f0;
          display: flex;
          justify-content: space-between;
          align-items: center;
        }

        .pagination-info {
          font-size: 14px;
          color: #64748b;
        }

        .pagination-info .bold {
          font-weight: 600;
          color: #334155;
        }

        .pagination-buttons {
          display: flex;
          gap: 8px;
        }

        .btn-page {
          padding: 8px 16px;
          background-color: white;
          border: 1px solid #cbd5e1;
          border-radius: 6px;
          font-size: 14px;
          color: #475569;
          cursor: pointer;
          transition: background-color 0.2s, border-color 0.2s;
        }

        .btn-page:hover {
          background-color: #f8fafc;
          border-color: #94a3b8;
        }

        .btn-page:disabled {
          opacity: 0.5;
          cursor: not-allowed;
        }

        /* Modal */
        .modal-overlay {
          position: fixed;
          inset: 0;
          background-color: rgba(0, 0, 0, 0.5);
          display: flex;
          align-items: center;
          justify-content: center;
          padding: 16px;
          z-index: 50;
        }

        .modal {
          background-color: white;
          border-radius: 8px;
          box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
          max-width: 600px;
          width: 100%;
          max-height: 90vh;
          overflow-y: auto;
        }

        .modal-header {
          padding: 16px 24px;
          border-bottom: 1px solid #e2e8f0;
        }

        .modal-header h2 {
          font-size: 18px;
          font-weight: bold;
          color: #1e293b;
          margin: 0;
        }

        .modal-body {
          padding: 24px;
        }

        .modal-footer {
          padding: 16px 24px;
          border-top: 1px solid #e2e8f0;
          display: flex;
          justify-content: flex-end;
          gap: 12px;
        }

        .form-grid {
          display: grid;
          grid-template-columns: 1fr;
          gap: 20px;
        }

        .form-row {
          display: grid;
          grid-template-columns: 1fr 1fr;
          gap: 16px;
        }

        .form-group {
          display: flex;
          flex-direction: column;
          gap: 8px;
        }

        .form-group label {
          font-size: 14px;
          font-weight: 500;
          color: #475569;
        }

        .form-control {
          padding: 8px 12px;
          border: 1px solid #cbd5e1;
          border-radius: 6px;
          font-size: 14px;
          color: #1e293b;
          width: 100%;
          transition: border-color 0.2s, box-shadow 0.2s;
        }

        .form-control:focus {
          outline: none;
          border-color: #3b82f6;
          box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.3);
        }

        textarea.form-control {
          min-height: 80px;
          resize: vertical;
        }

        .btn-secondary {
          background-color: white;
          color: #475569;
          border: 1px solid #cbd5e1;
          padding: 8px 16px;
          border-radius: 6px;
          font-weight: 500;
          cursor: pointer;
          transition: background-color 0.2s, border-color 0.2s;
        }

        .btn-secondary:hover {
          background-color: #f8fafc;
          border-color: #94a3b8;
        }

        /* Responsive */
        @media (max-width: 768px) {
          .toolbar {
            flex-direction: column;
            align-items: stretch;
          }

          .toolbar-left {
            flex-direction: column;
          }

          .form-row {
            grid-template-columns: 1fr;
          }
        }
      `
