import { useState, useEffect } from 'react';
import { Search, ChevronDown, ChevronUp, Calendar, Users, Clock, Edit, Trash2, Plus, Check, X } from 'lucide-react';

export default function Test() {
  const [formations, setFormations] = useState([
    { id: 1, titre: "Développement React Avancé", description: "Formation complète sur les concepts avancés de React", date: "2025-04-15", duree: 21, places: 12, inscrits: 8, statut: "Programmée" },
    { id: 2, titre: "Leadership et Management d'Équipe", description: "Techniques de management pour les chefs d'équipe", date: "2025-05-10", duree: 14, places: 15, inscrits: 15, statut: "Complète" },
    { id: 3, titre: "Excel pour RH", description: "Utilisation avancée d'Excel pour les professionnels RH", date: "2025-04-22", duree: 7, places: 20, inscrits: 5, statut: "Programmée" },
    { id: 4, titre: "Communication Professionnelle", description: "Améliorer sa communication en milieu professionnel", date: "2025-06-05", duree: 14, places: 18, inscrits: 12, statut: "Programmée" },
    { id: 5, titre: "RGPD et Conformité", description: "Les bases de la conformité RGPD pour les équipes RH", date: "2025-03-25", duree: 7, places: 25, inscrits: 20, statut: "Terminée" }
  ]);

  const [filteredFormations, setFilteredFormations] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortConfig, setSortConfig] = useState({ key: 'date', direction: 'asc' });
  const [modalOpen, setModalOpen] = useState(false);
  const [currentFormation, setCurrentFormation] = useState({
    id: null, titre: '', description: '', date: '', duree: 0, places: 0, inscrits: 0, statut: 'Programmée'
  });
  const [activeTab, setActiveTab] = useState('toutes');
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);

  // Filtrer les formations
  useEffect(() => {
    let result = [...formations];
    
    // Filtre par statut
    if (activeTab !== 'toutes') {
      result = result.filter(formation => {
        if (activeTab === 'programmees') return formation.statut === 'Programmée';
        if (activeTab === 'completes') return formation.statut === 'Complète';
        if (activeTab === 'terminees') return formation.statut === 'Terminée';
        return true;
      });
    }
    
    // Filtre par recherche
    if (searchTerm) {
      result = result.filter(formation => 
        formation.titre.toLowerCase().includes(searchTerm.toLowerCase()) ||
        formation.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    
    // Tri
    result.sort((a, b) => {
      if (a[sortConfig.key] < b[sortConfig.key]) {
        return sortConfig.direction === 'asc' ? -1 : 1;
      }
      if (a[sortConfig.key] > b[sortConfig.key]) {
        return sortConfig.direction === 'asc' ? 1 : -1;
      }
      return 0;
    });
    
    setFilteredFormations(result);
  }, [formations, searchTerm, sortConfig, activeTab]);

  const requestSort = (key) => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });
  };

  const handleAddNew = () => {
    setCurrentFormation({
      id: null,
      titre: '',
      description: '',
      date: '',
      duree: 0,
      places: 0,
      inscrits: 0,
      statut: 'Programmée'
    });
    setModalOpen(true);
  };

  const handleEdit = (formation) => {
    setCurrentFormation({...formation});
    setModalOpen(true);
  };

  const handleDelete = (id) => {
    setFormations(formations.filter(formation => formation.id !== id));
    setConfirmDeleteId(null);
  };

  const handleSave = () => {
    if (currentFormation.id) {
      // Édition
      setFormations(formations.map(formation => 
        formation.id === currentFormation.id ? currentFormation : formation
      ));
    } else {
      // Création
      const newId = Math.max(...formations.map(f => f.id)) + 1;
      setFormations([...formations, {...currentFormation, id: newId}]);
    }
    setModalOpen(false);
  };

  const getStatusColor = (statut) => {
    switch(statut) {
      case 'Programmée': return 'bg-blue-100 text-blue-800';
      case 'Complète': return 'bg-yellow-100 text-yellow-800';
      case 'Terminée': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('fr-FR', options);
  };

  return (
    <div className="bg-gray-50 min-h-screen p-6">
      <div className="max-w-6xl mx-auto bg-white rounded-lg shadow">
        {/* Header */}
        <div className="p-6 border-b border-gray-200">
          <h1 className="text-2xl font-bold text-gray-800">Gestion des Formations</h1>
          <p className="text-gray-600 mt-1">Interface d'administration pour gérer les formations de l'entreprise</p>
        </div>

        {/* Toolbar */}
        <div className="p-4 border-b border-gray-200 flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
          <div className="flex flex-col md:flex-row gap-3 w-full md:w-auto">
            <div className="relative">
              <input
                type="text"
                placeholder="Rechercher une formation..."
                className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 w-full md:w-64"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <Search className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
            </div>
            <div className="flex gap-2">
              <button 
                className={`px-4 py-2 rounded-lg font-medium ${activeTab === 'toutes' ? 'bg-blue-600 text-white' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}
                onClick={() => setActiveTab('toutes')}
              >
                Toutes
              </button>
              <button 
                className={`px-4 py-2 rounded-lg font-medium ${activeTab === 'programmees' ? 'bg-blue-600 text-white' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}
                onClick={() => setActiveTab('programmees')}
              >
                Programmées
              </button>
              <button 
                className={`px-4 py-2 rounded-lg font-medium ${activeTab === 'completes' ? 'bg-blue-600 text-white' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}
                onClick={() => setActiveTab('completes')}
              >
                Complètes
              </button>
              <button 
                className={`px-4 py-2 rounded-lg font-medium ${activeTab === 'terminees' ? 'bg-blue-600 text-white' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}
                onClick={() => setActiveTab('terminees')}
              >
                Terminées
              </button>
            </div>
          </div>
          <button 
            className="bg-blue-600 text-white px-4 py-2 rounded-lg flex items-center gap-2 hover:bg-blue-700 transition-colors"
            onClick={handleAddNew}
          >
            <Plus size={20} />
            <span>Nouvelle formation</span>
          </button>
        </div>

        {/* Table */}
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer" onClick={() => requestSort('titre')}>
                  <div className="flex items-center gap-1">
                    Titre
                    {sortConfig.key === 'titre' && (
                      sortConfig.direction === 'asc' ? <ChevronUp size={16} /> : <ChevronDown size={16} />
                    )}
                  </div>
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer" onClick={() => requestSort('date')}>
                  <div className="flex items-center gap-1">
                    Date
                    {sortConfig.key === 'date' && (
                      sortConfig.direction === 'asc' ? <ChevronUp size={16} /> : <ChevronDown size={16} />
                    )}
                  </div>
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer" onClick={() => requestSort('duree')}>
                  <div className="flex items-center gap-1">
                    Durée
                    {sortConfig.key === 'duree' && (
                      sortConfig.direction === 'asc' ? <ChevronUp size={16} /> : <ChevronDown size={16} />
                    )}
                  </div>
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer" onClick={() => requestSort('places')}>
                  <div className="flex items-center gap-1">
                    Places
                    {sortConfig.key === 'places' && (
                      sortConfig.direction === 'asc' ? <ChevronUp size={16} /> : <ChevronDown size={16} />
                    )}
                  </div>
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Statut
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredFormations.length > 0 ? (
                filteredFormations.map((formation) => (
                  <tr key={formation.id} className="hover:bg-gray-50">
                    <td className="px-6 py-4">
                      <div className="flex flex-col">
                        <span className="font-medium text-gray-900">{formation.titre}</span>
                        <span className="text-sm text-gray-500">{formation.description}</span>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex items-center">
                        <Calendar size={16} className="text-gray-400 mr-2" />
                        <span>{formatDate(formation.date)}</span>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex items-center">
                        <Clock size={16} className="text-gray-400 mr-2" />
                        <span>{formation.duree} heures</span>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex items-center">
                        <Users size={16} className="text-gray-400 mr-2" />
                        <span>{formation.inscrits}/{formation.places}</span>
                        {formation.inscrits === formation.places && (
                          <span className="ml-2 inline-block w-2 h-2 bg-yellow-500 rounded-full"></span>
                        )}
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <span className={`px-3 py-1 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusColor(formation.statut)}`}>
                        {formation.statut}
                      </span>
                    </td>
                    <td className="px-6 py-4">
                      {confirmDeleteId === formation.id ? (
                        <div className="flex gap-2">
                          <button onClick={() => handleDelete(formation.id)} className="text-red-600 hover:text-red-900 flex items-center gap-1">
                            <Check size={16} /> Confirmer
                          </button>
                          <button onClick={() => setConfirmDeleteId(null)} className="text-gray-600 hover:text-gray-900 flex items-center gap-1">
                            <X size={16} /> Annuler
                          </button>
                        </div>
                      ) : (
                        <div className="flex gap-2">
                          <button onClick={() => handleEdit(formation)} className="text-blue-600 hover:text-blue-900">
                            <Edit size={18} />
                          </button>
                          <button onClick={() => setConfirmDeleteId(formation.id)} className="text-red-600 hover:text-red-900">
                            <Trash2 size={18} />
                          </button>
                        </div>
                      )}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={6} className="px-6 py-4 text-center text-gray-500">
                    Aucune formation trouvée
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        {/* Pagination */}
        <div className="px-6 py-4 border-t border-gray-200 flex items-center justify-between">
          <div className="text-sm text-gray-700">
            Affichage de <span className="font-medium">{filteredFormations.length}</span> formation(s)
          </div>
          <div className="flex gap-2">
            <button className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed">
              Précédent
            </button>
            <button className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed">
              Suivant
            </button>
          </div>
        </div>
      </div>

      {/* Modal */}
      {modalOpen && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4">
          <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full">
            <div className="p-6 border-b border-gray-200">
              <h2 className="text-xl font-bold text-gray-800">
                {currentFormation.id ? 'Modifier la formation' : 'Ajouter une formation'}
              </h2>
            </div>
            <div className="p-6">
              <div className="grid grid-cols-1 gap-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Titre de la formation
                  </label>
                  <input 
                    type="text" 
                    className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    value={currentFormation.titre}
                    onChange={(e) => setCurrentFormation({...currentFormation, titre: e.target.value})}
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Description
                  </label>
                  <textarea 
                    className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    rows="3"
                    value={currentFormation.description}
                    onChange={(e) => setCurrentFormation({...currentFormation, description: e.target.value})}
                  ></textarea>
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Date
                    </label>
                    <input 
                      type="date" 
                      className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                      value={currentFormation.date}
                      onChange={(e) => setCurrentFormation({...currentFormation, date: e.target.value})}
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Durée (heures)
                    </label>
                    <input 
                      type="number" 
                      className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                      value={currentFormation.duree}
                      onChange={(e) => setCurrentFormation({...currentFormation, duree: parseInt(e.target.value) || 0})}
                    />
                  </div>
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Nombre de places
                    </label>
                    <input 
                      type="number" 
                      className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                      value={currentFormation.places}
                      onChange={(e) => setCurrentFormation({...currentFormation, places: parseInt(e.target.value) || 0})}
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Nombre d'inscrits
                    </label>
                    <input 
                      type="number" 
                      className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                      value={currentFormation.inscrits}
                      onChange={(e) => setCurrentFormation({...currentFormation, inscrits: parseInt(e.target.value) || 0})}
                    />
                  </div>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Statut
                  </label>
                  <select 
                    className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    value={currentFormation.statut}
                    onChange={(e) => setCurrentFormation({...currentFormation, statut: e.target.value})}
                  >
                    <option value="Programmée">Programmée</option>
                    <option value="Complète">Complète</option>
                    <option value="Terminée">Terminée</option>
                  </select>
                </div>
              </div>
            </div>
            <div className="p-6 border-t border-gray-200 flex justify-end gap-3">
              <button 
                className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
                onClick={() => setModalOpen(false)}
              >
                Annuler
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded-md text-sm font-medium hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                onClick={handleSave}
              >
                {currentFormation.id ? 'Mettre à jour' : 'Ajouter'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
