import React, { useState } from "react";
import PropTypes from "prop-types";

export default function CardPersonnels({ color = "light" }) {
  const [searchTerm, setSearchTerm] = useState("");
  const [personnels, setPersonnels] = useState([
    {
      id: 1,
      nom: "Jean Dupont",
      dateNaissance: "1990-05-12",
      departement: "Informatique",
    },
    {
      id: 2,
      nom: "Sophie Martin",
      dateNaissance: "1985-09-23",
      departement: "Ressources Humaines",
    },
    {
      id: 3,
      nom: "Ali Ben Salah",
      dateNaissance: "1992-11-02",
      departement: "Finance",
    },
  ]);

  // Fonction pour gérer la recherche
  const filteredPersonnels = personnels.filter((personnel) =>
    personnel.nom.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Fonction pour ajouter un nouveau personnel (exemple)
  const handleAddPersonnel = () => {
    const newPersonnel = {
      id: personnels.length + 1,
      nom: "Nouveau Personnel",
      dateNaissance: "2000-01-01",
      departement: "Nouveau Département",
    };
    setPersonnels([...personnels, newPersonnel]);
  };

  // Fonction pour gérer les actions (Voir, Modifier, Supprimer)
  const handleAction = (action, id) => {
    switch (action) {
      case "voir":
        alert(`Voir le personnel avec l'ID ${id}`);
        break;
      case "modifier":
        alert(`Modifier le personnel avec l'ID ${id}`);
        break;
      case "supprimer":
        setPersonnels(personnels.filter((personnel) => personnel.id !== id));
        break;
      default:
        break;
    }
  };

  return (
    <div
      className={
        "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
        (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
      }
    >
      <div className="rounded-t mb-0 px-4 py-3 border-0">
        <div className="flex flex-wrap items-center justify-between">
          <div className="relative w-full px-4 max-w-full flex-grow flex-1">
            <h3
              className={
                "font-semibold text-lg " +
                (color === "light" ? "text-blueGray-700" : "text-white")
              }
            >
              Liste des personnels
            </h3>
          </div>
          <div className="flex items-center space-x-4">
            {/* Barre de recherche */}
            <input
              type="text"
              placeholder="Rechercher par nom..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className={
                "px-3 py-2 border rounded-lg focus:outline-none " +
                (color === "light"
                  ? "border-blueGray-300 text-blueGray-700"
                  : "border-lightBlue-700 bg-lightBlue-800 text-white")
              }
            />&nbsp &nbsp &nbsp
            {/* Bouton Ajouter un personnel */}
            <button
              onClick={handleAddPersonnel}
              className={
                "px-4 py-2 rounded-lg font-semibold text-white bg-blue-500 hover:bg-blue-600 focus:outline-none"
              }
            >
              Ajouter un personnel
            </button>
          </div>
        </div>
      </div>
      <div className="block w-full overflow-x-auto">
        <table className="items-center w-full bg-transparent border-collapse">
          <thead>
            <tr>
              {[
                "Id",
                "Nom et prénom",
                "Date de naissance",
                "Départements",
                "Action",
              ].map((header, index) => (
                <th
                  key={index}
                  className={
                    "px-6 align-middle border border-solid py-3 text-xs uppercase border-l-0 border-r-0 whitespace-nowrap font-semibold text-left " +
                    (color === "light"
                      ? "bg-blueGray-50 text-blueGray-500 border-blueGray-100"
                      : "bg-lightBlue-800 text-lightBlue-300 border-lightBlue-700")
                  }
                >
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filteredPersonnels.map((personnel) => (
              <tr key={personnel.id}>
                <td className="px-6 py-4 border-b border-gray-200 text-sm">
                  {personnel.id}
                </td>
                <td className="px-6 py-4 border-b border-gray-200 text-sm">
                  {personnel.nom}
                </td>
                <td className="px-6 py-4 border-b border-gray-200 text-sm">
                  {personnel.dateNaissance}
                </td>
                <td className="px-6 py-4 border-b border-gray-200 text-sm">
                  {personnel.departement}
                </td>
                <td className="px-6 py-4 border-b border-gray-200 text-sm">
                  <button
                    onClick={() => handleAction("voir", personnel.id)}
                    className="bg-green-500 text-white px-2 py-1 rounded mr-2"
                  >
                    Voir
                  </button>
                  <button
                    onClick={() => handleAction("modifier", personnel.id)}
                    className="bg-yellow-500 text-white px-2 py-1 rounded mr-2"
                  >
                    Modifier
                  </button>
                  <button
                    onClick={() => handleAction("supprimer", personnel.id)}
                    className="bg-red-500 text-white px-2 py-1 rounded"
                  >
                    Supprimer
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

CardPersonnels.propTypes = {
  color: PropTypes.string,
};
