import React from "react";
import { Switch, Route, Redirect } from "react-router-dom";

// components
import Navbar from "components/Navbars/AuthNavbar.js";
import FooterSmall from "components/Footers/FooterSmall.js";

// views
import Login from "views/auth/Login.js";
import Forget from "views/auth/forget";
import Register from "views/auth/Register.js";

export default function Auth() {
  return (
    <>
      <Navbar transparent />
      <main>
        <section className="relative w-full h-full py-40 min-h-screen">
          {/* Background Overlay */}
          <div
            className="absolute top-0 w-full h-full bg-no-repeat bg-cover"
            style={{
              backgroundColor: "cadetblue",
              backgroundImage: `url(${require("assets/img/bcc.jpg").default})`,
              backgroundBlendMode: "multiply", // Ensures the color and image blend properly
              backgroundSize: "cover",
              backgroundPosition: "center",
              filter: "none", // Removes blur if applied
              backdropFilter: "none", // Ensures no background blur
            }}
          ></div>

          {/* Routes */}
          <Switch>
            <Route path="/auth/login" exact component={Login} />
            <Route path="/auth/forget" exact component={Forget} />
            <Route path="/auth/register" exact component={Register} />
            <Redirect from="/auth" to="/auth/login" />
          </Switch>

          <FooterSmall absolute />
        </section>
      </main>
    </>
  );
}
